<?phpsession_start();if($_SERVER['REQUEST_METHOD'] === 'POST'){    $errors = [];    $data = [];// step 1 validate the formif(empty($_POST['userEmail'])){    $errors['userEmail'] = 'email is required!';}if(empty($_POST['pass'])){    $errors['pass'] = 'password is required';}// CHECK IF THE errors Is not empty    if (!empty($errors)){        $data['success'] = false;        $data['errors'] = $errors;    }else{      $conn = mysqli_connect('sql108.infinityfree.com','if0_36676265','CzdXppFAt08jk','if0_36676265_galleryapp');     if(!$conn){        $data['success'] = false;        $data['message'] = 'Database connection problem';    }else{         $Password = mysqli_real_escape_string($conn, md5($_POST['pass']));         $userName = mysqli_real_escape_string($conn,$_POST['userEmail']);         $Query = "SELECT * FROM users WHERE  Email = ?  AND  Password = ?";         $stmt = $conn->prepare($Query);         $stmt->bind_param('ss',$userName,$Password);         $stmt->execute(); // execute the query         $result = $stmt->get_result(); // get the result         // check if the password or em         if($result->num_rows > 0){             $_SESSION['Email'] = $userName;             $data['success'] = true;             $data['message'] =  'success fully logged';         }else{ // pwding mali ang password or email or hindi register ang account             $data['success'] = false;             $data['message'] =  'your account is not found in our database!';         }         $stmt->close();// close the statement after use         $conn->close();// close the connection     }    }    echo json_encode($data);}