<?phpinclude '../Database/Connect.php';global $conn;session_start();if($_SERVER['REQUEST_METHOD'] === 'POST'){    $Password = mysqli_real_escape_string($conn, md5($_POST['Password']));    $userName = mysqli_real_escape_string($conn,$_POST['UserName']);    $Query = "SELECT * FROM users WHERE  Email = ?  AND  Password = ?";    $stmt = $conn->prepare($Query);    $stmt->bind_param('ss',$userName,$Password);    $stmt->execute(); // execute the query    $result = $stmt->get_result(); // get the result    if($result->num_rows > 0){       $_SESSION['Email'] = $userName;       echo "successLogin";    }else{        echo "You are not Registered";    }    $stmt->close();// close the statement after use}$conn->close();// close the connection