<?php// Check if the method is POSTif ($_SERVER['REQUEST_METHOD'] === 'POST') {    $errors = [];    $data = [];    // Validate all the inputs    if (empty($_POST['name'])) {        $errors['name'] = 'Name is required';    }    if (empty($_POST['email'])) {        $errors['email'] = 'Email is required';    }    if (empty($_POST['password'])) {        $errors['password'] = 'Password is required';    }    if (empty($_POST['confirm_pass'])) {        $errors['confirm_pass'] = 'Password confirmation is required';    } elseif ($_POST['password'] !== $_POST['confirm_pass']) {        $errors['confirm_pass'] = 'Passwords do not match';    }    // Checking if the errors array is not empty    if (!empty($errors)) {        $data['success'] = false;        $data['errors'] = $errors;    } else {        // Connect to the database        $conn = mysqli_connect('localhost','root','','galeryapp');        if (!$conn) {            $data['success'] = false;            $data['message'] = 'Database connection problem';        } else {            // Sanitize and fetch input            $Name = mysqli_real_escape_string($conn, $_POST['name']);            $Email = mysqli_real_escape_string($conn, $_POST['email']);            $Password = mysqli_real_escape_string($conn, md5($_POST['password']));            // Check if email already exists            $stmt = $conn->prepare('SELECT Email FROM users WHERE Email = ?');            $stmt->bind_param('s', $Email);            $stmt->execute();            $result = $stmt->get_result();            $email_ = $result->fetch_assoc();            if ($email_) {                $data['success'] = false;                $data['message'] = 'this email is already exist';            } else {                // Insert user credentials into the database                $Query = "INSERT INTO users (FullName, Email, Password) VALUES (?, ?, ?)";                $stmt = $conn->prepare($Query);                $stmt->bind_param('sss', $Name, $Email, $Password);                if ($stmt->execute()) {                    $data['success'] = true;                    $data['message'] = 'Successfully registered';                } else {                    $data['success'] = false;                    $data['message'] = 'Database error: ' . mysqli_error($conn);                }            }        }    }    // Convert the data into JSON    echo json_encode($data);}