<?phpinclude '../Database/Connect.php';global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST'){     $Name = mysqli_real_escape_string($conn,$_POST['Name']);     $Email = mysqli_real_escape_string($conn,$_POST['Email']);     $Password = mysqli_real_escape_string($conn, md5($_POST['Password']));     $ConfirmPass = mysqli_real_escape_string($conn,md5($_POST['ConfirmPass']));    $stmt = $conn->prepare('SELECT Email FROM users WHERE Email = ?');    $stmt->bind_param('s', $Email);    $stmt->execute();    $result = $stmt->get_result();    $email = $result->fetch_assoc();     // checking if the email is already exist     if($email){         echo "Email is already exist";     }else{         // making a query  to insert the credentials of the users         $Query  = "INSERT INTO users(FullName, Email, Password, Confirm_pass) VALUES (?,?,?,?)";         $stmt = $conn->prepare($Query);         $stmt->bind_param('ssss',$Name,$Email,$Password,$ConfirmPass);         if($stmt->execute()){             echo "Successfully Register";         }else{             echo "Error" . mysqli_error($conn);         }     }}