<?php// Enable error reportingerror_reporting(E_ALL);ini_set('display_errors', 1);include '../Database/Connect.php'; // Include your database connectionglobal $conn;if ($_SERVER['REQUEST_METHOD'] === 'POST') {    // Sanitize and fetch input    $Name = mysqli_real_escape_string($conn, $_POST['Name']);    $Email = mysqli_real_escape_string($conn, $_POST['Email']);    $Password = mysqli_real_escape_string($conn, md5($_POST['Password']));    $ConfirmPass = mysqli_real_escape_string($conn, md5($_POST['ConfirmPass']));    // Check if email already exists    $stmt = $conn->prepare('SELECT Email FROM users WHERE Email = ?');    $stmt->bind_param('s', $Email);    $stmt->execute();    $result = $stmt->get_result();    $email = $result->fetch_assoc();    if ($email) {        echo "Email is already exist";    } else {        // Insert user credentials into the database        $Query = "INSERT INTO users (FullName, Email, Password, Confirm_pass) VALUES (?, ?, ?, ?)";        $stmt = $conn->prepare($Query);        $stmt->bind_param('ssss', $Name, $Email, $Password, $ConfirmPass);        if ($stmt->execute()) {            echo "Successfully Register";        } else {            echo "Error: " . mysqli_error($conn);        }    }} else {    echo "Invalid request method.";}?>