<?phpinclude '../Database/Connect.php';global $conn;session_start();if($_SERVER['REQUEST_METHOD'] === 'POST'){    // Handling image upload    $path_dir = './uploadAlbum/';    $filePath = $_FILES['Image']['tmp_name'];    $fileName = $_FILES['Image']['name'];    $Email = $_SESSION['Email'];    $Destination  = $path_dir . $fileName;    move_uploaded_file($filePath,$Destination);    $ImageName = htmlspecialchars(basename($_FILES['Image']['name']));    $Title = mysqli_real_escape_string($conn,$_POST['Title']);    $Query = "INSERT INTO album(Title, Image,UserEmail) VALUES(?,?,?)";    $stmt = $conn->prepare($Query);    $stmt->bind_param('sss',$Title,$ImageName,$Email);    if($stmt->execute()){        echo "Pleas Wait...";    }else{        echo "ERROR" .mysqli_error($conn);    }    $stmt->close();    $conn->close();}