<?phpif($_SERVER['REQUEST_METHOD'] === 'POST'){    $data =[];    $errors =[];    // checking of validate the inputs    if(empty($_FILES['Image'])){        $errors['Image'] = 'please provide an image';    }    if(empty($_POST['Title'])){        $errors['Title'] = 'please enter your title';    }    if(!empty($errors)){        $data['success'] =false;        $data['errors'] = $errors;    }else{     // or make a Query to send the data in  our database        include '../Database/Connect.php';        global $conn;        session_start();            // Handling image upload            $path_dir = './uploadAlbum/';            $filePath = $_FILES['Image']['tmp_name'];            $fileName = $_FILES['Image']['name'];            $Email = $_SESSION['Email'];            $Destination  = $path_dir . $fileName;            move_uploaded_file($filePath,$Destination);            $ImageName = htmlspecialchars(basename($_FILES['Image']['name']));            $Title = mysqli_real_escape_string($conn,$_POST['Title']);            $Query = "INSERT INTO album(Title, Image,UserEmail) VALUES(?,?,?)";            $stmt = $conn->prepare($Query);            $stmt->bind_param('sss',$Title,$ImageName,$Email);            if($stmt->execute()){                $data['success'] = true;                $data['message'] = "uploading...";            }else{                $data['success'] = false;               $data['message'] =  mysqli_error($conn);            }            $stmt->close();            $conn->close();    }echo  json_encode($data);}