<?php$conn = mysqli_connect('sql108.infinityfree.com','if0_36676265','CzdXppFAt08jk','if0_36676265_galleryapp');if(!$conn){    die('<h1 class="text-danger">Connection Problem</h1>');}global $conn;if($_SERVER['REQUEST_METHOD'] === 'POST'){$data = [];$errors = [];// checking if we have error or the error is not empty    if (!empty($errors)){        $data['success'] = false;        $data['errors'] = $errors;    }else{        // else make a deletion of  the album and set the success to true        $data ['success'] = true;        $data['message'] = 'success!';        $ID = $_POST['Id'];        $Query = "DELETE FROM album WHERE Id = ?";        $stmt = $conn->prepare($Query);        $stmt->bind_param('i',$ID);        if($stmt->execute()){            $data['message'] = 'successfully deleted!';        }else{            $data['success'] = false;            $data['message'] = 'deleting failed'. mysqli_error($conn);        }    }    echo json_encode($data);}