<?php$conn = mysqli_connect('sql108.infinityfree.com','if0_36676265','CzdXppFAt08jk','if0_36676265_galleryapp');if(!$conn){    die('<h1 class="text-danger">Connection Problem</h1>');}if($_SERVER['REQUEST_METHOD'] === 'POST'){    $data = [];    $errors = [];    // check if we have an error then set the success to false    if(!empty($errors)){        $data['success'] = false;        $data['errors'] = $errors;    }else{        $id = $_POST['Id'];        $query = "DELETE FROM pictures WHERE id = ?";        $stmt = $conn->prepare($query);        $stmt->bind_param('i',$id);        if ($stmt->execute()){            $data['success'] = true;            $data['message'] = 'successfullyDelete';        }else{            $data['success'] = false;            $data['message'] = 'error' . mysqli_error($conn);        }    }echo json_encode($data);    $conn->close();}