<?phpinclude '../Database/Connect.php';global $conn;// check if the  request method is equal to postif($_SERVER['REQUEST_METHOD'] === 'POST'){ // Handel the upload to save category     $UploadDir = './PictureUpload/';     $fileName= $_FILES['picture']['name'];     $filePath = $_FILES['picture']['tmp_name'];     $destination = $UploadDir . $fileName;     //  moving the into Dir    move_uploaded_file($destination,$filePath);  $ImageName = htmlspecialchars(basename($_FILES['picture']['name']));    $id = $_POST['ID'];    //  Making Query  To update the image    $Query = "UPDATE pictures SET image = ? WHERE id = ?";    $stmt = $conn->prepare($Query);    $stmt->bind_param('si',$ImageName,$id);    if($stmt->execute()){        echo "Please Wait...";    }else{        echo "Error" . mysqli_error($conn);    }}