<?php$conn = mysqli_connect('sql108.infinityfree.com','if0_36676265','CzdXppFAt08jk','if0_36676265_galleryapp');if(!$conn){    die('<h1 class="text-danger">Connection Problem</h1>');}// check if the  request method is equal to postif($_SERVER['REQUEST_METHOD'] === 'POST'){    $data = [];    $errors = [];    if(!empty($errors)){        $data['success'] = false;        $data['errors'] = $errors;    }else{        // Handel the upload to save category        $UploadDir = './PictureUpload/';        $fileName= $_FILES['picture']['name'];        $filePath = $_FILES['picture']['tmp_name'];        $destination = $UploadDir . $fileName;        //  moving the into Dir        move_uploaded_file($destination,$filePath);        $ImageName = htmlspecialchars(basename($_FILES['picture']['name']));        $id = $_POST['ID'];        //  Making Query  To update the image        $Query = "UPDATE pictures SET image = ? WHERE id = ?";        $stmt = $conn->prepare($Query);        $stmt->bind_param('si',$ImageName,$id);        if($stmt->execute()){            $data['success'] = true;            $data['message'] = 'successfullyUpdate';        }else{            $data['success'] = false;            $data['message'] = "Error" . mysqli_error($conn);        }    }echo json_encode($data);}