<?php$conn = mysqli_connect('sql108.infinityfree.com','if0_36676265','CzdXppFAt08jk','if0_36676265_galleryapp');if(!$conn){    die('<h1 class="text-danger">Connection Problem</h1>');}if($_SERVER['REQUEST_METHOD'] === 'POST'){    $data = [];    $errors = [];    if (!empty($errors)){        $data['success'] = false;        $data['errors'] = $errors;    }else{        $id = $_POST['Id'];        $Title = mysqli_real_escape_string($conn,$_POST['Title']);         // check if the input file is not empty(if true make a query with image        if(!empty( $_FILES['image']['name'])){            // if the condition is true then make update the image            $image_dir = './uploadAlbum/';            $FilePath = $_FILES['image']['tmp_name'];            $fileName = $_FILES['image']['name'];            $Destination = $image_dir . $fileName;            move_uploaded_file($FilePath,$Destination);            $imageName = htmlspecialchars(basename($_FILES['image']['name']));            //make a query            $Query = "UPDATE album SET Image = ? ,Title = ? WHERE Id = ?";            $stmt = $conn->prepare($Query);            $stmt->bind_param('ssi',$imageName,$Title,$id);            if($stmt->execute()){                $data['message'] = 'album is successfully update';                $data['success'] = true;            }else{                $data['message'] = 'update is failed' .mysqli_error($conn);            }            $stmt->close();        }else{            // else you update the title only then make  query without the image update            $Query = "UPDATE album SET Title = ? WHERE Id = ?";            $stmt = $conn->prepare($Query);            $stmt->bind_param('si',$Title,$id);            if($stmt->execute()){                $data['message'] = 'album is successfully update';                $data['success'] = true;            }else{                $data['message'] = 'update is failed' .mysqli_error($conn);            }        }    }    echo json_encode($data);}